const Part = (props) => {
  return (
    <div>
    <p>{props.exercise} {props.number}</p>
    </div>
  )
}
const Header = (props) => {

  return  (
    <div>
      <h1>
        {props.course}
      </h1>
    </div>
  )
}
const Content = (props) => {  
  console.log(props.parts[0].name)
  return (
    <div>

        <p>{props.parts[0].name}</p>
        <Part exercise = {props.parts[0].name} number = {props.parts[0].exercises} />
      </div>
  )
}
const Total = (props) => {
  return (
  <div>
    <p>Number of exercises {props.exercises1 + props.exercises2 + props.exercises3}</p>
  </div>
  )
}


const App = () => {
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  return (
    <div>
      <p> {parts[0].name}</p>
      <Content parts={parts}/>



      {
        //<Content parts = {parts} />
      //<Total exercises1 = {parts[0].exercises} />
}
    </div>
  )
}

export default App
