import { useState } from 'react'
import { showFiltered } from './components/showFiltered'
import { showAll } from './components/showAll'

const App = () => {
  let text = "Hello world, welcome to the universe.";
  let result = text.includes("ME TO");
  console.log(result)

  const [newSearch, setNewSearch] = useState('')
  const [newPerson, setNewPerson] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 },
    { name: 'A', number: '1', id: 5 }
  ]) 

  const addPerson = (event) => {
    event.preventDefault()
    const names = persons.map(p => p["name"])
    console.log("map: ", names)
    let flag = false
    for (let i=0; i<persons.length; i++) {
      if (persons[i]["name"] == newPerson) {
        console.log("persons at i name: ",persons[i]["name"], "newPerson: ", newPerson);
        flag = true
      }
    }

    console.log("t/f: ", flag)
    if (flag == false) {
      const noteObject = {
        name: newPerson,
        number: newNumber
      }
      setPersons(persons.concat(noteObject))
      setNewPerson('')
      setNewNumber('')
    } else {
      window.alert(`${newPerson} is already added to phonebook`)
    }


    console.log("persons: ",persons)

  }

  const handlePersonInputChange = (event) => {
    console.log(event.target.value)
    setNewPerson(event.target.value)
  }

  const handleNumberInputChange = (event) => {
    console.log(event.target.value)
    setNewNumber(event.target.value)
  }

  const handleSearchInputChange = (event) => {
    console.log(event.target.value)
    setNewSearch(event.target.value)
  }

  const filtered = persons.filter(d => d["name"].toUpperCase().includes(newSearch.toUpperCase()))
  console.log("filtered: ", filtered)

  return (
    <div>
      <h2>Phonebook</h2>

      <form onSubmit="">
        <div>
          filter shown with: 
          <input 
          value={newSearch}
          onChange={handleSearchInputChange}
          />

        <div>
          <button type="submit">add</button>
        </div>
        </div>
      </form>

      <form onSubmit={addPerson}>
        <div>
          name: 
          <input 
          value={newPerson}
          onChange={handlePersonInputChange}
          />
          <div>
            number:
            <input 
            value={newNumber}
            onChange={handleNumberInputChange}
            />
          </div>  
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {
      newSearch === ""
      ? persons.map(p => 
        <div key = {p["name"]}>
          {p["name"]} {p["number"]}
        </div>
        )
      : filtered.map(p => 
        <div key = {p["name"]}>
          {p["name"]} {p["number"]}
        </div>
        )
        }
    </div>
  )
}

export default App
