import { useState, useEffect } from 'react'
import { showFiltered } from './components/showFiltered'
import Note from './components/Note'
import ShowAll from './components/showAll'
import ShowFiltered from './components/showFiltered';
import AddPerson2 from './components/AddPerson2';
import personService from './services/persons';


const App = () => {

  let text = "Hello world, welcome to the universe.";
  let result = text.includes("ME TO");
  console.log(result)
  //below three lines added for the example from material.
  const [notes, setNotes] = useState([])
  const [newSearch, setNewSearch] = useState('')
  const [currentFilter, setCurrentFilter] = useState('')
  const [newPerson, setNewPerson] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [persons, setPersons] = useState([]) 

  useEffect(() => {
    console.log('effect')
    personService.getAll()
      .then(response => {
        console.log('promise fulfilled')
        //setNotes(response.data)
        setPersons(response)
        console.log("Notes from response data: ",response)
      })
  }, [])
  console.log('render', notes.length, 'notes')





  const setFilter = (event) => {
    event.preventDefault()
    setCurrentFilter(newSearch)
    console.log("successfully set a new filter")

  }

  const createNewId = () => {
    return Math.max(...persons.map(p=>p.id))+1
  }

  const addPerson = (event) => {
    event.preventDefault()
    const names = persons.map(p => p["name"])
    console.log("map: ", names)
    let flag = false
    for (let i=0; i<persons.length; i++) {
      if (persons[i]["name"] == newPerson) {
        console.log("persons at i name: ",persons[i]["name"], "newPerson: ", newPerson);
        flag = true
      }
    }

    console.log("t/f: ", flag)
    if (flag == false) {
      const noteObject = {
        name: newPerson,
        number: newNumber,
        id: createNewId()
      }
      personService.create(noteObject)
      setPersons(persons.concat(noteObject))
      setNewPerson('')
      setNewNumber('')
    } else {
      //window.alert(`${newPerson} is already added to phonebook`)
      if(window.confirm(`${newPerson} is already in the phonebook, replace the old number with the new one?`)) {
        console.log("Number change")
        const id = persons.filter(p => p["name"] === newPerson)[0]["id"]
        const noteObject = {
          name: newPerson,
          number: newNumber,
          id: id
        }
        personService
          .update(id,noteObject)
          .then(response => {
            console.log(response)
            setPersons(...persons, response)
            })
      } else {
        console.log("No number change");
      }
    }


    console.log("persons: ",persons)

  }

  const handlePersonInputChange = (event) => {
    console.log(event.target.value)
    setNewPerson(event.target.value)
  }

  const handleNumberInputChange = (event) => {
    console.log(event.target.value)
    setNewNumber(event.target.value)
  }

  const handleSearchInputChange = (event) => {
    console.log(event.target.value)
    setNewSearch(event.target.value)
  }

  const handleDeleteById = (event) => {
    event.preventDefault()
    const id = Number(event.target.value)
    const person = persons.filter(p => p.id === id)[0]
    const name = person.name
    console.log("person ", person)
    console.log("person.name ", name)
    if(window.confirm(`Do you want to delete ${name}`)) {
      console.log("Deleting ",id)
      personService
        .deleteById(id)
        .then(response => 
          {
          console.log(response)
          setPersons(persons.filter(p => p.id !== id))
          }
        )
    }
  }

  const filtered = persons.filter(d => d["name"].toUpperCase().includes(currentFilter.toUpperCase()))
  console.log("filtered: ", filtered)

  return (
    <div>
      <h2>Phonebook</h2>

      <form onSubmit={setFilter}>
        <div>
          filter shown with: 
          <input 
          value={newSearch}
          onChange={handleSearchInputChange}
          />

        <div>
          <button type="submit">add</button>
        </div>
        </div>
      </form>

      <AddPerson2 
      handlePersonInputChange={handlePersonInputChange} 
      newNumber={newNumber} 
      handleNumberInputChange={handleNumberInputChange} 
      newPerson={newPerson}
      addPerson={addPerson}
      />  

      <h2>Numbers</h2>
      {
      newSearch === "" && currentFilter === ""
      ? <ShowAll persons = {persons} handleDeleteById={handleDeleteById} />
      : <ShowFiltered filtered = {filtered} />
      }

    </div>
  )
}

export default App
