import { useState, useEffect } from 'react'
import Note from './components/Note'
import Notification from './components/Notification'
import entryService from './services/entries'

const App = () => {
  const [entries, setEntries] = useState([
  {
      "name": "Arto Hellas",
      "number": "040-123456",
      "id": 1
    }
  ])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [showAll, setShowAll] = useState(true)
  const [errorMessage, setErrorMessage] = useState(null)

  useEffect(() => {
    entryService
      .getAll()
      .then(initialEntries => {
        setEntries(initialEntries)
      })
  }, [])

  const addNote = (event) => {
    event.preventDefault()

    const noteObject = {
      name: newName,
      number: newNumber,
    }

    if(entries.find(note => note.name === newName)) {
      console.log("Name already in database!")
      const note = entries.find(note => note.name === newName)
      const id = note.id
      entryService
        .update(id, noteObject).then(returnedNote => {
          setEntries(entries.map(note => note.id !== id ? note : returnedNote))
          setNewName('')
          setNewNumber('')
        })
        .catch(error => {
          const error_message = "400 (Bad request). " + error.response.data.error
          setErrorMessage(
            error_message
          )
          setTimeout(() => {
            setErrorMessage(null)
          }, 5000)

        })

    } else {
    entryService
      .create(noteObject)
        .then(returnedNote => {
        setEntries(entries.concat(returnedNote))
        setNewName('')
      })
      .catch(error => {
        const error_message = "400 (Bad request). " + error.response.data.error
        setErrorMessage(
          error_message
        )
        setTimeout(() => {
          setErrorMessage(null)
        }, 5000)
      })



    }

  }

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

   const updateNumbdddder = id => {
      const note = entries.find(n => n.id === id)
      const changedNote = { ...note, number: 2 }

      entryService
        .update(id, changedNote).then(returnedNote => {
          setEntries(entries.map(note => note.id !== id ? note : returnedNote))
        })
        .catch(error => {
          setErrorMessage(
            `Note '${note.content}' was already removed from server`
          )
          setTimeout(() => {
            setErrorMessage(null)
          }, 5000)
          setEntries(entries.filter(n => n.id !== id))
        })
    }

    const deleteById = id => {
      entryService
        .deleteById(id).then(returnedNote => {
          console.log("Delete entry of id ${id}??", returnedNote);
          setEntries(entries.filter(note => note.id != id))
        })
    }

  return (
    <div>
      <Notification message={errorMessage} />
      <ul>
        <ul>
          {entries.map(note => 
            <Note
              key={note.id}
              note={note}
              deleteById={() => deleteById(note.id)}
            />
          )}
        </ul>
      </ul>
      <form onSubmit={addNote}>
        <input placeholder="Name" value={newName} onChange={handleNameChange} />
        <input placeholder="Number" value={newNumber} onChange={handleNumberChange} />
        <button type="submit">save</button>
      </form>
    </div>
  )
}

export default App
